export default function StoreSystem() {
    const [selectedStore, setSelectedStore] = useState(null);
    const [isModalOpen, setIsModalOpen] = useState(false);

    const breadcrumbItems = [
        { label: 'Hệ thống cửa hàng', path: '/he-thong-cua-hang' }
    ];

    const handleStoreClick = (store) => {
        setSelectedStore(store);
        setIsModalOpen(true);
    };

    const closeModal = () => {
        setIsModalOpen(false);
        setSelectedStore(null);
    };

    return (
        <>
            <Breadcrumb items={breadcrumbItems} />
            <div className="store-system-page">
                <div className="container">
                    <h1 className="page-title">Hệ thống cửa hàng</h1>
                    <div className="store-grid">
                        {stores.map((store) => (
                            <div key={store.id} className="store-card" onClick={() => handleStoreClick(store)}>
                                <div className="store-image">
                                    <img src={store.image} alt={store.name} />
                                </div>
                                <div className="store-info">
                                    <h3>{store.name}</h3>
                                    <p className="store-address">{store.address}</p>
                                    <p className="store-hours">Giờ mở cửa: {store.hours}</p>
                                    <button className="view-details-btn">Xem chi tiết</button>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>

                {isModalOpen && selectedStore && (
                    <div className="store-modal-overlay" onClick={closeModal}>
                        <div className="store-modal" onClick={e => e.stopPropagation()}>
                            <button className="close-modal" onClick={closeModal}>×</button>
                            <div className="modal-content">
                                <div className="modal-header">
                                    <h2>{selectedStore.name}</h2>
                                </div>
                                <div className="modal-body">
                                    <div className="store-details">
                                        <div className="store-info-details">
                                            <div className="info-item">
                                                <i className="fas fa-map-marker-alt"></i>
                                                <span>{selectedStore.address}</span>
                                            </div>
                                            <div className="info-item">
                                                <i className="fas fa-phone"></i>
                                                <span>{selectedStore.phone}</span>
                                            </div>
                                            <div className="info-item">
                                                <i className="fas fa-envelope"></i>
                                                <span>{selectedStore.email}</span>
                                            </div>
                                            <div className="info-item">
                                                <i className="fas fa-clock"></i>
                                                <span>Giờ mở cửa: {selectedStore.hours}</span>
                                            </div>
                                        </div>
                                        <div className="store-map">
                                            <iframe
                                                src={selectedStore.mapUrl}
                                                width="100%"
                                                height="300"
                                                style={{ border: 0 }}
                                                allowFullScreen=""
                                                loading="lazy"
                                                referrerPolicy="no-referrer-when-downgrade"
                                            ></iframe>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                )}
            </div>
        </>
    );
} 